import React, { useEffect, useRef } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-draw/dist/leaflet.draw.css';
import L from 'leaflet';
import 'leaflet-draw';

const WorldMap = () => {
  const mapRef = useRef();

  useEffect(() => {
    const map = mapRef.current;

    if (map) {
      const drawnItems = new L.FeatureGroup();
      map.leafletElement.addLayer(drawnItems);

      const drawControl = new L.Control.Draw({
        draw: {
          marker: false,
          polyline: false,
          circle: false,
          circlemarker: false,
          polygon: {
            allowIntersection: false,
            drawError: {
              color: '#e1e100', // Color the shape will turn when intersects
              message: "<strong>Oh snap!<strong> you can't draw that!", // Message that will show when intersect
            },
            shapeOptions: {
              color: '#3388ff',
            },
          },
        },
        edit: {
          featureGroup: drawnItems,
        },
      });

      map.leafletElement.addControl(drawControl);

      map.leafletElement.on(L.Draw.Event.CREATED, function (event) {
        const layer = event.layer;
        drawnItems.addLayer(layer);
      });

      return () => {
        map.leafletElement.removeControl(drawControl);
        map.leafletElement.off(L.Draw.Event.CREATED);
      };
    }
  }, [mapRef]);

  return (
    <MapContainer
      ref={mapRef}
      center={[0, 0]}
      zoom={2}
      style={{ height: '500px', width: '100%' }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
    </MapContainer>
  );
};

export default WorldMap;
