import React, { useEffect, useRef, useState } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-draw/dist/leaflet.draw.css';
import L from 'leaflet';
import 'leaflet-draw';

const WorldMap = () => {
  const mapRef = useRef();
  const [drawControl, setDrawControl] = useState(null);
  const [drawingMode, setDrawingMode] = useState(null);

  useEffect(() => {
    const map = mapRef.current?.leafletElement;

    if (map) {
      const drawnItems = new L.FeatureGroup();
      map.addLayer(drawnItems);

      const control = new L.Control.Draw({
        draw: {
          marker: false,
          polyline: false,
          circle: false,
          circlemarker: false,
          polygon: {
            allowIntersection: false,
            drawError: {
              color: '#e1e100',
              message: "<strong>Oh snap!<strong> you can't draw that!",
            },
            shapeOptions: {
              color: '#3388ff',
            },
          },
          rectangle: {
            shapeOptions: {
              color: '#3388ff',
            },
          },
        },
        edit: {
          featureGroup: drawnItems,
        },
      });

      setDrawControl(control);

      map.addControl(control);

      map.on(L.Draw.Event.CREATED, function (event) {
        const layer = event.layer;
        drawnItems.addLayer(layer);
      });
    }

    return () => {
      if (map && drawControl) {
        map.removeControl(drawControl);
        map.off(L.Draw.Event.CREATED);
      }
    };
  }, [mapRef, drawControl]);

  const clearShapes = () => {
    const map = mapRef.current?.leafletElement;
    map.eachLayer((layer) => {
      if (!layer._url) {
        map.removeLayer(layer);
      }
    });
  };

  const setDrawOptions = (mode) => {
    setDrawingMode(mode);
  };

  useEffect(() => {
    if (drawControl && drawingMode) {
      const options = {
        polyline: false,
        polygon: false,
        rectangle: false,
        circle: false,
      };

      options[drawingMode] = true;
      drawControl.setDrawingOptions(options);
    }
  }, [drawControl, drawingMode]);

  return (
    <div>
      <div style={{ marginBottom: '10px' }}>
        <button onClick={() => setDrawOptions('polygon')}>Draw Polygon</button>
        <button onClick={() => setDrawOptions('rectangle')}>Draw Rectangle</button>
        <button onClick={() => clearShapes()}>Clear Shapes</button>
      </div>
      <MapContainer
        ref={mapRef}
        center={[0, 0]}
        zoom={2}
        style={{ height: '500px', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
      </MapContainer>
    </div>
  );
};

export default WorldMap;
